#' FinalFit
#'
#' 
#' @param data .
#' @param explanatory .
#' @param outcome .
#' @param overalltime .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
finalfit <- function(
    data,
    explanatory,
    outcome,
    overalltime) {

    if ( ! requireNamespace('jmvcore'))
        stop('finalfit requires jmvcore to be installed (restart may be required)')

    if ( ! missing(explanatory)) explanatory <- jmvcore::resolveQuo(jmvcore::enquo(explanatory))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(overalltime)) overalltime <- jmvcore::resolveQuo(jmvcore::enquo(overalltime))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(explanatory), explanatory, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(overalltime), overalltime, NULL))

    for (v in explanatory) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- finalfitOptions$new(
        explanatory = explanatory,
        outcome = outcome,
        overalltime = overalltime)

    analysis <- finalfitClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
