#' GGStatsPlot
#'
#' 
#' @param data .
#' @param dep .
#' @param group .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
statsplot <- function(
    data,
    dep,
    group) {

    if ( ! requireNamespace('jmvcore'))
        stop('statsplot requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL))


    options <- statsplotOptions$new(
        dep = dep,
        group = group)

    analysis <- statsplotClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
