#' Summary of Continious Variables
#'
#' 
#' @param data the data as a data frame
#' @param vars a string naming the variables from \code{data} that contains
#'   the continious values used for the report
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
summary <- function(
    data,
    vars) {

    if ( ! requireNamespace('jmvcore'))
        stop('summary requires jmvcore to be installed (restart may be required)')

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- summaryOptions$new(
        vars = vars)

    analysis <- summaryClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
