#' survival
#'
#' 
#' @param data .
#' @param factor .
#' @param outcome .
#' @param overalltime .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text4} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text5} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text6} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text7} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text8} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$summary} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$summary2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
survival <- function(
    data,
    factor,
    outcome,
    overalltime) {

    if ( ! requireNamespace('jmvcore'))
        stop('survival requires jmvcore to be installed (restart may be required)')

    if ( ! missing(factor)) factor <- jmvcore::resolveQuo(jmvcore::enquo(factor))
    if ( ! missing(outcome)) outcome <- jmvcore::resolveQuo(jmvcore::enquo(outcome))
    if ( ! missing(overalltime)) overalltime <- jmvcore::resolveQuo(jmvcore::enquo(overalltime))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(factor), factor, NULL),
            `if`( ! missing(outcome), outcome, NULL),
            `if`( ! missing(overalltime), overalltime, NULL))


    options <- survivalOptions$new(
        factor = factor,
        outcome = outcome,
        overalltime = overalltime)

    analysis <- survivalClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
